declare module '*.module.scss' {
    const styles: { [className: string]: string };
    export = styles;
}

declare module '*.png' {
    const image: any;
    export = image;
}

declare module '*.jpg' {
    const image: any;
    export = image;
}

declare module '*.gif' {
    const image: any;
    export = image;
}
